CREATE TABLE ADMINISTRATORS (ID NUMBER(38) NOT NULL, EMAIL VARCHAR2(255), PASSWORD VARCHAR2(255), USERNAME VARCHAR2(255), VERSION NUMBER(38), PRIMARY KEY (ID));
CREATE TABLE PATIENTS (ID NUMBER(38) NOT NULL, DOB DATE, EMAIL VARCHAR2(255), GENDER VARCHAR2(255), PASSWORD VARCHAR2(255), PHONE VARCHAR2(255), SSN VARCHAR2(255), STATUS VARCHAR2(255), USERNAME VARCHAR2(255), VERSION NUMBER(38), CITY VARCHAR2(255), COUNTRY VARCHAR2(255), "STATE" VARCHAR2(255), STREET1 VARCHAR2(255), STREET2 VARCHAR2(255), ZIP VARCHAR2(255), FIRSTNAME VARCHAR2(255), LASTNAME VARCHAR2(255), MIDDLENAME VARCHAR2(255), PRIMARY KEY (ID));
CREATE TABLE PATIENTS_RECORDS (PATIENT_ID NUMBER(38) NOT NULL, RECORDS_ID NUMBER(38) NOT NULL, PRIMARY KEY (PATIENT_ID, RECORDS_ID));
CREATE TABLE PHYSICIANS (ID NUMBER(38) NOT NULL, EMAIL VARCHAR2(255), PASSWORD VARCHAR2(255), PHONE VARCHAR2(255), USERNAME VARCHAR2(255), VERSION NUMBER(38), FIRSTNAME VARCHAR2(255), LASTNAME VARCHAR2(255), MIDDLENAME VARCHAR2(255), PRIMARY KEY (ID));
CREATE TABLE PRESCRIPTIONS (ID NUMBER(38) NOT NULL, DATE_PRESCRIBED DATE, DOSAGE VARCHAR2(255), DRUG VARCHAR2(255), FREQUENCY VARCHAR2(255), INSTRUCTIONS VARCHAR2(255), REFILLS_REMAINING NUMBER(38), VERSION NUMBER(38), PRIMARY KEY (ID));
CREATE TABLE RECORDS (ID NUMBER(38) NOT NULL, CREATE_DATE DATE, DIAGNOSIS VARCHAR2(255), NOTES VARCHAR2(255), SYMPTOMS VARCHAR2(255), VERSION NUMBER(38), DIASTOLIC_BLOOD_PRESSURE NUMBER(38), HEIGHT NUMBER(38), PULSE NUMBER(38), SYSTOLIC_BLOOD_PRESSURE NUMBER(38), TEMPERATURE NUMBER(38), WEIGHT NUMBER(38), PATIENT_ID NUMBER(38) NOT NULL, PHYSICIAN_ID NUMBER(38) NOT NULL, PRIMARY KEY (ID));
CREATE TABLE RECORDS_PRESCRIPTIONS (RECORD_ID NUMBER(38) NOT NULL, PRESCRIPTIONS_ID NUMBER(38) NOT NULL, PRIMARY KEY (PRESCRIPTIONS_ID, RECORD_ID));
CREATE TABLE "SEQUENCE" (SEQ_NAME VARCHAR2(50) NOT NULL, SEQ_COUNT NUMBER(15), PRIMARY KEY (SEQ_NAME));










